sys_script_client.script:
function onSubmit() {
     var action = g_form.getActionName();
	 //alert('You pressed ' + action); 
	var fieldState = g_form.getControl('state');

	if(fieldState == null){

		//strange! should not be null but ok nothing to do...
	}else{

		if(fieldState.changed){
			//alert("oh yes it changed! New value: " + g_form.getValue('state') + "!!!");
			if(g_form.getValue('state') == '15'){
				//put to assignement group value
				var assignment_groupValue = g_form.getValue('assignment_group');
				if(assignment_groupValue!=null){
					g_form.setValue('u_reference_2', assignment_groupValue);
				}else{
					//alert('strange! Assignment Group should not be empty!!');
				}
			}else{
				//empty it!
				g_form.setValue('u_reference_2', null);
			}
		}else{
			//alert("ok our field did not change");
		}
	}


}
