var CIInfo = Class.create();
CIInfo.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	amountSameClass: function(){
		var ret = "uninitalized";
		var ci_id = this.getParameter('sysparm_ci_id');
		var cmdb = new GlideRecord('cmdb');
		if(cmdb!=null && ci_id!=null){
			cmdb.addQuery("sys_id",ci_id);
			cmdb.query();
			if(cmdb.hasNext()){
				cmdb.next();
				var sys_class_name = cmdb.sys_class_name;
				if(sys_class_name!=null){
					var cmdb2 = new GlideRecord('cmdb');
					//we assume, if we just got a value here, that this value is valid!
					cmdb2.addQuery("sys_class_name", sys_class_name);
					cmdb2.query();
					ret = "Total " + cmdb2.getRowCount() + " CIs of the same class (" + sys_class_name + ") exist in the system";
					//ret = "ok, we have a sys_class_name: '" + sys_class_name + "'!";
				}else{
					ret = "ah!! no class name for ci with id '" + ci_id + "'!";
				}
			}else{
				ret = "ah! no record with sys_id '" + ci_id + "'!";
			}
		}else{

			if(target==null){
				ret = "table 'hange_request' not found";
			}else if(ci_id == null){
				ret = "param 'sysparm_ci_id' not set";
			}else{

			}

		}

		return ret;
	}
});
